mapper.select:
select
    *, DATE_FORMAT(add_time, "%Y-%m-%d %H:%i:%s") as add_time_show, DATE_FORMAT(update_time, "%Y-%m-%d %H:%i:%s") as update_time_show
from label where user_id = #{userId}

mapper.update:
update
    label
set
    content = #{content}, update_time = #{updateTime}
where
    id = #{id}
